import React from 'react';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    height: '300px',   
    '& > *': {
      margin: theme.spacing(1),
    },
  },
  description: {
    fontSize: '30px',
    fontFamily: 'arial',
    textAlign: 'center',
    marginTop: '35px'
  },
  title: {
    fontSize: '40px',
    width: '100%',
    border: '1px solid black',
    textAlign: 'center'      
  },
  form: {
      display: 'flex',
      alignItems: 'center'
  }
}));

const BatchPanel = () => {
    const classes = useStyles();
    return(
        <Container className={classes.root}>
            <div className={classes.title}>BatchCreator</div>
            <div className={classes.description}>
            This is a small app that takes a number of profile records
            and stores therm in a SQLite database. You can create batches of
            profile records by line numbers. The point of the app is to demonstrate
            knowledge of ag-record. 
            </div>
            <form action="" className={classes.form}>
                <label className="batch-panel__label__number-of-rows">
                    Number of Rows to be Inserted into Database
                </label>
                <input type="text" placeholder="Number of batchs" />
                <input type="text" placeholder="Batch Name" />
                <Button 
                    variant="contained" 
                    size="medium" 
                    color="primary"
                >
                    Primary
                </Button>
            </form>
        </Container>
    )
}

export default BatchPanel;
