import React, { useState, useEffect } from 'react';
import { render } from 'react-dom';
import { AgGridColumn, AgGridReact } from 'ag-grid-react';
import CircularProgress from '@material-ui/core/CircularProgress';
import axios from 'axios';

import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-alpine.css';

const styles = {
  width: '80%',
  height: '100vh',
  margin: '50px auto',
  borderTop: '1px solid black',
  '&:hover': {
    'cursor': 'pointer'
  }  
}

const App = () => {
    const [rowData, setRowData] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const columnFields = [
      'email',
      'name.title',
      'name.first',
      'name.last',
      'dob.age',
      'location.city',
      'location.state',
      'location.country',
      'gender'
    ]

    useEffect(() => {
      setIsLoading(true);
      const getProfileData = async () => {
        const rowData = await axios.get(
          'https://randomuser.me/api/?format=json&results=500'
        );
        setRowData(rowData.data.results); 
      }
      getProfileData();
      setIsLoading(false);      
    }, [])

    return (
        <div className="ag-theme-alpine" style={styles}>
            {isLoading && <CircularProgress />}
            <AgGridReact
                data-testid="parentGrid"
                rowData={rowData}>
                {rowData && columnFields.map((index, fieldName) => {
                  return (
                    <AgGridColumn field="name"></AgGridColumn>
                  )
                }) }
            </AgGridReact>
        </div>
    );
};
export default App;
